mapper.select:
SELECT *
FROM sqlconnect.sys_user
WHERE user_code = #{userCode}
  AND user_password = #{userPassword}
  AND user_state = '1'

SELECT sys_user.user_name, travel_member.*
FROM sqlconnect.sys_user, sqlconnect.travel_member
WHERE sys_user.user_id = #{id}
AND travel_member.id = sys_user.user_id;
